package com.emsi.Tp2;


import org.openqa.selenium.*;
import org.openqa.selenium.chrome.ChromeDriver;
import org.openqa.selenium.chrome.ChromeOptions;
import org.openqa.selenium.support.ui.ExpectedConditions;
import org.openqa.selenium.support.ui.WebDriverWait;
import org.testng.annotations.TestInstance;

import java.time.Duration;
import java.util.List;


@TestInstance(TestInstance.Lifecycle.PER_CLASS)
@TestMethodOrder(MethodOrderer.OrderAnnotation.class)
public class PlanTestRechercheGoogle {

    private WebDriver driver;
    private WebDriverWait wait;
    private static final String GOOGLE_URL = "https://www.google.com";

    @BeforeAll
    public void setupClass() {
        System.out.println("╔════════════════════════════════════════════════╗");
        System.out.println("║   PLAN DE TEST - RECHERCHE GOOGLE             ║");
        System.out.println("║   Suite de tests automatisés avec Selenium    ║");
        System.out.println("╚════════════════════════════════════════════════╝\n");
    }

    @BeforeEach
    public void setup() {
        // Configuration Chrome anti-détection
        ChromeOptions options = new ChromeOptions();
        options.addArguments("--disable-blink-features=AutomationControlled");
        options.addArguments("--start-maximized");
        options.addArguments("--disable-notifications");
        options.setExperimentalOption("excludeSwitches", new String[]{"enable-automation"});
        options.setExperimentalOption("useAutomationExtension", false);

        driver = new ChromeDriver(options);
        wait = new WebDriverWait(driver, Duration.ofSeconds(15));

        // Masquer webdriver
        driver.executeScript("Object.defineProperty(navigator, 'webdriver', {get: () => undefined})");
    }

    /**
     * TC-001 : Recherche simple avec mot-clé valide
     * Priorité : HAUTE
     */
    @Test
    @Order(1)
    @DisplayName("TC-001 : Recherche avec mot-clé valide")
    public void testRechercheSimple() throws InterruptedException {
        System.out.println("\n[TC-001] Début du test : Recherche simple");

        // Étape 1 : Ouvrir Google
        driver.get(GOOGLE_URL);
        accepterCookies();

        // Étape 2 : Localiser la barre de recherche
        WebElement barreRecherche = wait.until(
                ExpectedConditions.presenceOfElementLocated(By.name("q"))
        );

        // Étape 3 : Saisir le mot-clé
        String motCle = "Selenium WebDriver";
        barreRecherche.sendKeys(motCle);
        Thread.sleep(500);

        // Étape 4 : Soumettre la recherche
        barreRecherche.submit();

        // Étape 5 : Attendre les résultats
        wait.until(ExpectedConditions.titleContains(motCle));
        Thread.sleep(2000);

        // Assertions
        String titre = driver.getTitle();
        System.out.println("   Titre obtenu : " + titre);

        assertTrue(titre.toLowerCase().contains(motCle.toLowerCase()),
                "Le titre doit contenir le mot-clé");
        assertFalse(titre.startsWith("http"),
                "Le titre ne doit pas être une URL");

        // Vérifier la présence de résultats
        List<WebElement> resultats = driver.findElements(By.cssSelector("div.g"));
        System.out.println("   Nombre de résultats : " + resultats.size());
        assertTrue(resultats.size() >= 3,
                "Au moins 3 résultats doivent être affichés");

        System.out.println("[TC-001] ✓ Test réussi\n");
    }

    /**
     * TC-002 : Soumission avec champ vide
     * Priorité : MOYENNE
     */
    @Test
    @Order(2)
    @DisplayName("TC-002 : Soumission avec champ vide")
    public void testChampVide() throws InterruptedException {
        System.out.println("\n[TC-002] Début du test : Champ vide");

        driver.get(GOOGLE_URL);
        accepterCookies();

        WebElement barreRecherche = wait.until(
                ExpectedConditions.presenceOfElementLocated(By.name("q"))
        );

        // Soumettre sans rien saisir
        barreRecherche.submit();
        Thread.sleep(2000);

        // Vérifier qu'on reste sur la page d'accueil
        String urlActuelle = driver.getCurrentUrl();
        System.out.println("   URL actuelle : " + urlActuelle);

        assertTrue(urlActuelle.contains("google.com"),
                "Doit rester sur Google");
        assertFalse(urlActuelle.contains("search?q="),
                "Ne doit pas effectuer de recherche");

        System.out.println("[TC-002] ✓ Test réussi\n");
    }

    /**
     * TC-003 : Recherche avec longue chaîne
     * Priorité : BASSE
     */
    @Test
    @Order(3)
    @DisplayName("TC-003 : Recherche avec longue chaîne (500 caractères)")
    public void testLonguChaine() throws InterruptedException {
        System.out.println("\n[TC-003] Début du test : Longue chaîne");

        driver.get(GOOGLE_URL);
        accepterCookies();

        WebElement barreRecherche = wait.until(
                ExpectedConditions.presenceOfElementLocated(By.name("q"))
        );

        // Générer une chaîne de 500 caractères
        String longueChaine = "A".repeat(500);
        System.out.println("   Longueur de la chaîne : " + longueChaine.length());

        barreRecherche.sendKeys(longueChaine);
        Thread.sleep(500);

        // Vérifier la valeur saisie
        String valeurSaisie = barreRecherche.getAttribute("value");
        System.out.println("   Longueur saisie : " + valeurSaisie.length());

        assertNotNull(valeurSaisie, "La valeur ne doit pas être null");
        assertTrue(valeurSaisie.length() > 0, "Une valeur doit être saisie");

        // Soumettre
        barreRecherche.submit();
        Thread.sleep(2000);

        // Vérifier qu'une recherche a été effectuée
        String urlActuelle = driver.getCurrentUrl();
        assertTrue(urlActuelle.contains("search?q=") || urlActuelle.contains("google.com"),
                "La recherche doit être effectuée ou limitée");

        System.out.println("[TC-003] ✓ Test réussi\n");
    }

    /**
     * TC-004 : Recherche avec caractères spéciaux
     * Priorité : MOYENNE
     */
    @Test
    @Order(4)
    @DisplayName("TC-004 : Recherche avec caractères spéciaux")
    public void testCaracteresSpeciaux() throws InterruptedException {
        System.out.println("\n[TC-004] Début du test : Caractères spéciaux");

        driver.get(GOOGLE_URL);
        accepterCookies();

        WebElement barreRecherche = wait.until(
                ExpectedConditions.presenceOfElementLocated(By.name("q"))
        );

        // Caractères spéciaux
        String caracteresSpeciaux = "@#$%^&*()[]{}";
        System.out.println("   Caractères testés : " + caracteresSpeciaux);

        barreRecherche.sendKeys(caracteresSpeciaux);
        Thread.sleep(500);
        barreRecherche.submit();
        Thread.sleep(2000);

        // Vérifier que la page a changé (recherche effectuée)
        String urlActuelle = driver.getCurrentUrl();
        System.out.println("   URL : " + urlActuelle);

        assertNotEquals(GOOGLE_URL, urlActuelle,
                "L'URL doit avoir changé après la recherche");

        System.out.println("[TC-004] ✓ Test réussi\n");
    }

    /**
     * TC-005 : Navigation vers la recherche d'images
     * Priorité : HAUTE
     */
    @Test
    @Order(5)
    @DisplayName("TC-005 : Navigation vers recherche d'images")
    public void testNavigationImages() throws InterruptedException {
        System.out.println("\n[TC-005] Début du test : Navigation Images");

        driver.get(GOOGLE_URL);
        accepterCookies();

        // Chercher le lien "Images"
        try {
            WebElement lienImages = wait.until(
                    ExpectedConditions.elementToBeClickable(
                            By.xpath("//a[contains(text(), 'Images') or @aria-label='Recherche d\\'images Google']")
                    )
            );

            System.out.println("   Clic sur le lien Images");
            lienImages.click();
            Thread.sleep(2000);

            // Vérifier l'URL
            String urlActuelle = driver.getCurrentUrl();
            System.out.println("   URL : " + urlActuelle);

            assertTrue(urlActuelle.contains("images.google") || urlActuelle.contains("imghp"),
                    "L'URL doit contenir 'images.google' ou 'imghp'");

            // Effectuer une recherche d'image
            WebElement barreRechercheImage = wait.until(
                    ExpectedConditions.presenceOfElementLocated(By.name("q"))
            );

            barreRechercheImage.sendKeys("Nature");
            barreRechercheImage.submit();
            Thread.sleep(2000);

            System.out.println("[TC-005] ✓ Test réussi\n");

        } catch (Exception e) {
            System.out.println("[TC-005] ⚠ Lien Images non trouvé ou changement d'UI");
            System.out.println("   Message : " + e.getMessage());
        }
    }

    /**
     * TC-006 : Vérification des suggestions automatiques
     * Priorité : MOYENNE
     */
    @Test
    @Order(6)
    @DisplayName("TC-006 : Suggestions de recherche (auto-complétion)")
    public void testSuggestionsRecherche() throws InterruptedException {
        System.out.println("\n[TC-006] Début du test : Suggestions automatiques");

        driver.get(GOOGLE_URL);
        accepterCookies();

        WebElement barreRecherche = wait.until(
                ExpectedConditions.presenceOfElementLocated(By.name("q"))
        );

        // Saisir progressivement
        String recherche = "Selen";
        for (char c : recherche.toCharArray()) {
            barreRecherche.sendKeys(String.valueOf(c));
            Thread.sleep(200);
        }

        // Attendre l'apparition des suggestions
        Thread.sleep(1500);

        try {
            // Chercher la liste de suggestions
            WebElement listeSuggestions = wait.until(
                    ExpectedConditions.presenceOfElementLocated(
                            By.cssSelector("ul[role='listbox'], div[role='listbox']")
                    )
            );

            List<WebElement> suggestions = listeSuggestions.findElements(
                    By.cssSelector("li[role='option'], div[role='option']")
            );

            System.out.println("   Nombre de suggestions : " + suggestions.size());

            assertTrue(suggestions.size() > 0,
                    "Des suggestions doivent apparaître");

            System.out.println("[TC-006] ✓ Test réussi\n");

        } catch (Exception e) {
            System.out.println("[TC-006] ⚠ Suggestions non détectées");
            System.out.println("   Message : " + e.getMessage());
        }
    }

    /**
     * TC-007 : Test du bouton "J'ai de la chance"
     * Priorité : BASSE
     */
    @Test
    @Order(7)
    @DisplayName("TC-007 : Bouton J'ai de la chance")
    public void testBoutonJaiDeLaChance() throws InterruptedException {
        System.out.println("\n[TC-007] Début du test : J'ai de la chance");

        driver.get(GOOGLE_URL);
        accepterCookies();

        WebElement barreRecherche = wait.until(
                ExpectedConditions.presenceOfElementLocated(By.name("q"))
        );

        barreRecherche.sendKeys("Wikipedia");
        Thread.sleep(1000);

        try {
            // Chercher le bouton "J'ai de la chance"
            WebElement boutonChance = driver.findElement(
                    By.xpath("//input[@value=\"J'ai de la chance\" or @aria-label=\"J'ai de la chance\"]")
            );

            System.out.println("   Clic sur 'J'ai de la chance'");
            boutonChance.click();

            // Attendre la redirection
            Thread.sleep(3000);

            String urlFinale = driver.getCurrentUrl();
            System.out.println("   URL finale : " + urlFinale);

            assertFalse(urlFinale.contains("google.com/search"),
                    "Ne doit pas afficher la page de résultats Google");

            System.out.println("[TC-007] ✓ Test réussi\n");

        } catch (Exception e) {
            System.out.println("[TC-007] ⚠ Bouton 'J'ai de la chance' non accessible");
            System.out.println("   Message : " + e.getMessage());
        }
    }

    /**
     * Méthode utilitaire pour accepter les cookies
     */
    private void accepterCookies() {
        try {
            WebElement boutonCookies = wait.until(
                    ExpectedConditions.elementToBeClickable(
                            By.xpath("//button[contains(., 'Tout accepter') or contains(., 'Accept all') or contains(., 'J\\'accepte')]")
                    )
            );
            boutonCookies.click();
            Thread.sleep(1000);
        } catch (Exception e) {
            // Cookies déjà acceptés ou pas de popup
        }
    }

    @AfterEach
    public void tearDown() {
        if (driver != null) {
            driver.quit();
        }
    }

    @AfterAll
    public void tearDownClass() {
        System.out.println("\n╔════════════════════════════════════════════════╗");
        System.out.println("║   FIN DE LA SUITE DE TESTS                     ║");
        System.out.println("╚════════════════════════════════════════════════╝");
    }
}

